mapper.select:
SELECT openId, userName, userAvatar, createTime, phone, token, privilegeId
FROM UserInfo
WHERE openId = #{openId};

mapper.insert:
INSERT INTO UserHealth(
    openId,
    createTime,
    nowAddress,
    longitude,
    latitude
) VALUES(
            #{openId},
            #{createTime},
            #{nowAddress},
            #{longitude},
            #{latitude}
        );

mapper.update:
update UserDrugDetail
set subscriptionStatus = #{status}
where openId = #{openId}
  and drugUuid = #{drugUuid}

mapper.insert:
INSERT INTO UserFood(
